import React from 'react';
import copy from 'clipboard-copy'
import {
  RowContainer,
  Input,
  TextButton,
  SearchInput,
  Textarea,
  ContainerForIcon,
  Title,
} from '../../pages/commonStyles';
import Image from './Image'
import Loupe from '../../images/Loupe.svg';
import Copy from '../../images/copy.svg';
import { useTheme } from '@mui/material';
import { useSnackbar } from 'notistack';
import { isExtension } from '../../utils/utils';

const ImagesPath = {
  closedEye: "data:image/svg+xml;base64,",
  eye: "data:image/svg+xml;base64,",
}

const InputWithComponent = ({
  type = 'text',
  value,
  onChange,
  autoFocus = false,
  disabled = false,
  placeholder = '',
  ComponentToShow,
  style = {},
  containerStyle,
  autoComplete = "off",
  onKeyDown = () => {}
}: {
  type?: string;
  value: string;
  onChange: any;
  disabled?: boolean;
  autoFocus?: boolean,
  placeholder?: string;
  ComponentToShow: any;
  style?: any
  containerStyle?: any
  autoComplete?: string
  onKeyDown?: (e: any) => void
}) => {
  return (
    <RowContainer >
      <Input
        type={type}
        value={value}
        autoFocus={autoFocus}
        onChange={onChange}
        placeholder={placeholder}
        style={style}
        autoComplete={autoComplete}
        disabled={disabled}
        onKeyDown={onKeyDown}
      />
      <div
        style={{
          position: 'absolute',
          right: '0',
          top: '50%',
          transform: 'translateY(-50%)',
        }}
      >
        {ComponentToShow}
      </div>
    </RowContainer>
  );
};

const InputWithEye = ({
  showPassword,
  onEyeClick,
  ...props
}: {
  type: string;
  value: string;
  onChange: any;
  placeholder: string;
  showPassword: boolean;
  onEyeClick: () => void;
  style?: any
  containerStyle?: any
  autoComplete?: string
  onKeyDown?: (e: any) => void
}) => {
  return (
    <InputWithComponent
      autoFocus={true}
      ComponentToShow={
        <Image
          style={{
            padding: '1.6rem 2rem 1.4rem 2rem',
            cursor: 'pointer',
            height: '4.5rem',
          }}
          onClick={onEyeClick}
          src={showPassword ? ImagesPath.closedEye : ImagesPath.eye}
          alt="eye"
        />
      }
      {...props}
    />
  );
};

const InputWithPaste = ({
  onPasteClick,
  ...props
}: {
  type?: string;
  disabled?: boolean;
  autoFocus?: boolean;
  value: string;
  onChange: any;
  placeholder: string;
  onPasteClick: () => void;
  style?: any
  autoComplete?: string
  containerStyle?: any
  onKeyDown?: (e: any) => void
}) => {
  const theme = useTheme();

  return (
    <InputWithComponent
      ComponentToShow={
        isExtension ? null :
        <TextButton
          color={theme.customPalette.blue.new}
          onClick={() => {
            onPasteClick()
          }}
          
        >
          Paste
        </TextButton>
      }
      {...props}
    />
  );
};

const InputWithMax = ({
  onMaxClick,
  maxText,
  ...props
}: {
  type: string;
  value: string;
  onChange: any;
  maxText: string;
  placeholder: string;
  onMaxClick: () => void;
  style?: any
  containerStyle?: any
}) => {
  const theme = useTheme();

  return (
    <InputWithComponent
      ComponentToShow={
        <>
        <Title fontSize={'1rem'} color={theme.customPalette.grey.dark}>Max:</Title>
        <TextButton
          color={theme.customPalette.blue.new}
          onClick={() => {
            onMaxClick()
          }}
          
        >
          {maxText}
        </TextButton>
        </>
      }
      {...props}
    />
  );
};

const SearchInputWithLoupe = ({
  type,
  value,
  onChange,
  placeholder,
  ComponentToShow,
}: {
  type: string;
  value: string;
  onChange: any;
  placeholder: string;
  ComponentToShow: any;
}) => {
  return (
    <RowContainer >
      <SearchInput
        type={type}
        value={value}
        onChange={onChange}
        placeholder={placeholder}
      />
      <div
        style={{
          position: 'absolute',
          right: '2rem',
          top: '50%',
          transform: 'translateY(-40%)',
        }}
      >
        {ComponentToShow}
      </div>
    </RowContainer>
  );
};

const InputWithSearch = ({
  onSearchClick,
  ...props
}: {
  type: string;
  value: string;
  onChange: any;
  placeholder: string;
  onSearchClick: () => void;
}) => {
  return (
    <SearchInputWithLoupe
      ComponentToShow={
        <img
          
          onClick={onSearchClick}
          src={Loupe}
          alt="search icon"
        />
      }
      {...props}
    />
  );
};

const TextareaWithComponent = ({
  type = 'text',
  value,
  onChange = () => {},
  placeholder = '',
  ComponentToShow,
  height = '6rem',
  style = {}
}: {
  type?: string;
  value: any;
  onChange?: any;
  placeholder?: string;
  ComponentToShow: any;
  height?: string;
  style?: any
}) => {
  return (
    <RowContainer >
      <Textarea
        type={type}
        value={value}
        onChange={onChange}
        placeholder={placeholder}
        height={height}
        style={style}
      />
      <div
        style={{
          position: 'absolute',
          right: '2rem',
          top: '50%',
          transform: 'translateY(-50%)',
        }}
      >
        <ContainerForIcon>{ComponentToShow}</ContainerForIcon>
      </div>
    </RowContainer>
  );
};

const TextareaWithCopy = ({
  onCopy,
  ...props
}: {
  height?: string;
  type?: string;
  value: any;
  onChange?: any;
  placeholder?: string;
  style?: any
  onCopy?: () => {}
}) => {
  const { enqueueSnackbar } = useSnackbar();

  return (
    <TextareaWithComponent
      ComponentToShow={
        <img
          style={{
            padding: '1.6rem 2rem 1.4rem 2rem',
            cursor: 'pointer',
            height: '4.5rem',
          }}
          onClick={() => {
            if (!!onCopy) {
              onCopy()
            } else {
              copy(props.value)
            }

            enqueueSnackbar("Copied!", { variant: 'success' });
          }}
          src={Copy}
          alt="copy"
        />
      }
      {...props}
    />
  );
};

export { InputWithEye, InputWithPaste, InputWithMax, InputWithSearch, TextareaWithCopy };
export default InputWithComponent;
